mapper.select:
select count(*)
from member
where user_id = #{userId}

mapper.update:

update member 
set user_name=#{userName}, nickname=#{nickName}, birth_day=#{birthDay}, gender=#{gender},
email=#{email}
where user_id=#{userId}

update member
set status='N'
where user_id = #{userId}

mapper.select:
select count(*)
from c_scrap
where user_id = #{userId}

select count(*)
from h_scrap
where user_id = #{userId}

select count(*)
from l_scrap
where user_id = #{userId}
